import Foundation
import Moya

let giphyProvider = MoyaProvider<Giphy>()

public enum Giphy {
    case upload(gif: Data)
}

extension Giphy: TargetType {
    public var baseURL: URL { URL(string: "https://upload.giphy.com")! } // swiftlint:disable:this force_unwrapping
    public var path: String {
        switch self {
        case .upload:
            return "/v1/gifs"
        }
    }
    public var method: Moya.Method {
        switch self {
        case .upload:
            return .post
        }
    }
    public var task: Task {
        switch self {
        case let .upload(data):
            let multipartFormBodyParts = [MultipartFormBodyPart(provider: .data(data), name: "file", fileName: "gif.gif", mimeType: "image/gif")]
            let multipartFormData = MultipartFormData(fileManager: .default, boundary: nil, parts: multipartFormBodyParts)
            return .uploadCompositeMultipartFormData(multipartFormData, urlParameters: ["api_key": "dc6zaTOxFJmzC", "username": "Moya"])
        }
    }
    public var sampleData: Data {
        switch self {
        case .upload:
            return "{\"data\":{\"id\":\"your_new_gif_id\"},\"meta\":{\"status\":200,\"msg\":\"OK\"}}".data(using: String.Encoding.utf8)! // swiftlint:disable:this force_unwrapping
        }
    }

    public var headers: [String: String]? { nil }

    // swiftlint:disable:next force_unwrapping
    static let animatedBirdData = Data(base64Encoded: "", options: [])!
}
